  padding: $spacing-base;
  background-color: $bg-content;
  border: 1px solid $border-color;
  border-radius: $border-radius;
  transition: $transition-base;

  &:hover {
    border-color: $primary;
    box-shadow: 0 0 0 2px rgba($primary, 0.1);
  }
}

.agenda-item-time {
  font-weight: $font-weight-medium;
  color: $primary;
  background-color: rgba($primary, 0.1);
  padding: ($spacing-base * 0.25) ($spacing-base * 0.75);
  border-radius: $border-radius * 0.5;
}

.agenda-item-details {
  flex-grow: 1;
}

.agenda-item-title {
  font-size: 1rem;
  font-weight: $font-weight-medium;
  color: $text-primary;
  margin: 0;
}

.agenda-item-location {
  font-size: 0.875rem;
  color: $text-secondary;
  margin: ($spacing-base * 0.25) 0 0 0;
}

.agenda-item-link {
  color: $text-light;
  &:hover {
    color: $primary;
  }
}

.empty-agenda {
  padding: $spacing-base * 2;
  text-align: center;
  color: $text-secondary;
  background-color: $bg-content;
  border: 1px dashed $border-color;
  border-radius: $border-radius;
}
// Add these styles to your existing my-events.scss file

.calendar-and-agenda-container {
  display: flex;
  gap: 2rem; // Space between calendar and agenda
  align-items: flex-start;
}

.calendar-wrapper {
  // This is the key fix: it prevents the calendar from shrinking.
  // It will take up 2/3 of the available space.
  flex: 2;
  min-width: 0; // A flexbox hack to allow shrinking if the screen is too small
}

.agenda-wrapper {
  // The agenda will take up 1/3 of the space.
  flex: 1;
  min-width: 0;
}

